select

from SUC_USER

	
		and ORG_ID = #{orgId,jdbcType=BIGINT}
	
	
		and LOGIN_NAME = #{loginName,jdbcType=VARCHAR}
	
	
		and LOGIN_PSWD = #{loginPswd,jdbcType=VARCHAR}
	
	
		and PAY_PSWD = #{payPswd,jdbcType=VARCHAR}
	
	
		and SALT = #{salt,jdbcType=CHAR}
	
	
		and NICKNAME = #{nickname,jdbcType=VARCHAR}
	
	
		and FACE = #{face,jdbcType=VARCHAR}
	
	
		and REALNAME = #{realname,jdbcType=VARCHAR}
	
	
		and IS_VERIFIED_REALNAME = #{isVerifiedRealname,jdbcType=TINYINT}
	
	
		and IDCARD = #{idcard,jdbcType=VARCHAR}
	
	
		and IS_VERIFIED_IDCARD = #{isVerifiedIdcard,jdbcType=TINYINT}
	
	
		and EMAIL = #{email,jdbcType=VARCHAR}
	
	
		and IS_VERIFIED_EMAIL = #{isVerifiedEmail,jdbcType=TINYINT}
	
	
		and MOBILE = #{mobile,jdbcType=VARCHAR}
	
	
		and IS_VERIFIED_MOBILE = #{isVerifiedMobile,jdbcType=TINYINT}
	
	
		and QQ_ID = #{qqId,jdbcType=VARCHAR}
	
	
		and QQ_OPENID = #{qqOpenid,jdbcType=VARCHAR}
	
	
		and QQ_NICKNAME = #{qqNickname,jdbcType=VARCHAR}
	
	
		and QQ_FACE = #{qqFace,jdbcType=VARCHAR}
	
	
		and WX_ID = #{wxId,jdbcType=VARCHAR}
	
	
		and WX_OPENID = #{wxOpenid,jdbcType=VARCHAR}
	
	
		and WX_NICKNAME = #{wxNickname,jdbcType=VARCHAR}
	
	
		and WX_FACE = #{wxFace,jdbcType=VARCHAR}
	
	
		and IS_TESTER = #{isTester,jdbcType=TINYINT}
	
	
		and IS_LOCK = #{isLock,jdbcType=TINYINT}
	
	
		and PROMOTER_ID = #{promoterId,jdbcType=BIGINT}
	
	
		and MODIFIED_TIMESTAMP = #{modifiedTimestamp,jdbcType=BIGINT}
	
	
		and DOMAIN_ID = #{domainId,jdbcType=VARCHAR}


select count(*)
from SUC_USER
where ID = #{id,jdbcType=BIGINT}


