SELECT su.*, so.`NAME` FROM SUC_USER su LEFT JOIN SUC_ORG so ON su.ORG_ID=so.ID
and su.LOGIN_NAME LIKE '%${keys}%' OR su.NICKNAME LIKE '%${keys}%' OR su.REALNAME LIKE '%${keys}%' OR su.IDCARD LIKE '%${keys}%' OR su.EMAIL LIKE '%${keys}%' OR su.MOBILE LIKE '%${keys}%' OR su.QQ_NICKNAME LIKE '%${keys}%' OR su.WX_NICKNAME LIKE '%${keys}%' OR su.ID LIKE '%${keys}'
and ORG_ID = #{orgId,jdbcType=BIGINT}
